import React from 'react';

function Notes(props) {

    function crossOut(event) {

        let todoList = JSON.parse(localStorage.getItem('todo'));
        const index = todoList.findIndex(el => el.id === props.id);
        todoList[index].check = !todoList[index].check;
        localStorage.setItem('todo', JSON.stringify(todoList));
    }

    // function classCrossOut(event) {
    //     console.log(event.target)
    //     event.target.classList.toggle('checked');
    // }



    return (
        <div className="notes-container">
            <span onClick={crossOut} >
                <input 
                    type="checkbox"
                    checked={props.check} 
                    id={`chk${props.x}`} name="chkdemo"></input>
                <label className="check" id={props.id} htmlFor={`chk${props.x}`}></label>
            </span>

            <label className="text">{props.text}</label>
            <i className="button delete-button fa fa-times" aria-hidden="true" ></i>
        </div>
    );
}

export default Notes;
