import React, {useState} from 'react';

function Notes(props) {

    const [states, setStates] = useState(JSON.parse(localStorage.getItem('todo')));

    function crossOut(event) {
        const index = states.findIndex(el => el.id == event.target.id);
        states[index].check = !states[index].check;  

        localStorage.setItem('todo', JSON.stringify(states));
    }

    return (
        <div className="notes-container">
            <input type="checkbox" className={`checkbox ${props.check ? 'checked' : ''}`} id={`chk${props.x}`} name="chkdemo"></input>
            <label onClick={crossOut} className="check" id={props.id} htmlFor={`chk${props.x}`}></label>
            <label className="text">{props.text}</label>
            <i className="button delete-button fa fa-times" aria-hidden="true" ></i>
        </div>
    );
}

export default Notes;
